# Script to fix escaped characters in test_generation_service.py\nfile_path = 'tests/code2markdown/test_generation_service.py'\n\n# Read the file\nwith open(file_path, 'r', encoding='utf-8') as f:\n    lines = f.readlines()\n\n# Fix the escaped characters\nfixed_lines = []\nfor line in lines:\n    # Replace \\n with actual newline\n    line = line.replace('\\\\n', '\\n')\n    # Replace \\\" with actual quote\n    line = line.replace('\\\\\"', '\"')\n    # Replace \\\\ with actual backslash\n    line = line.replace('\\\\\\\\', '\\\\')\n    fixed_lines.append(line)\n\n# Write the fixed content back to the file\nwith open(file_path, 'w', encoding='utf-8') as f:\n    f.writelines(fixed_lines)\n\nprint(\"File fixed successfully.\")
